sys_script_client.script:
function onCellEdit(sysIDs, table, oldValues, newValue, callback) {

	function numberCallback(response){
		alert(response);
	}

	var saveAndClose = true;


	alert("The value of the field has changed from \"" + oldValues + "\" to \"" + newValue + "\"\n by: " + g_user.firstName + g_user.lastName);
	var ga = new GlideAjax('NumberFetcher');
	ga.addParam('sysparm_name','getNumber');
	ga.addParam('sysparm_sys_ID', sysIDs);
	ga.getXMLAnswer(numberCallback);

	setTimeout(function (){
	this.window.location.reload();
	}, 300);

	callback(saveAndClose); 
}
