sys_script_include.script:
var NumberFetcher = Class.create();
NumberFetcher.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

getNumber : function(){
	var IDs = this.getParameter('sysparm_sys_ID');
	var gr = new GlideRecord('x_58872_needit_need_it');

	var numbers = [];

	gr.addEncodedQuery('sys_idIN' + IDs);
	gr.query();	

	while(gr.next()){
		numbers.push(gr.changenumber.toString());
	}

	return JSON.stringify(numbers);
},	

    type: 'NumberFetcher'
});
